import Cors from "cors";
import initMiddleware from "../../lib/init-middleware";

// Initialize the cors middleware
const cors = initMiddleware(
  Cors({
    // Only allow requests with GET, POST and PUT
    methods: ["GET", "POST", "PUT"],
  })
);

export default async (req, res) => {
  // Run cors
  await cors(req, res);

  const signedUrl =
    "https://wellick-lease-image-s3-stage.s3.amazonaws.com/lease-image-1-1599786710182?AWSAccessKeyId=AKIAWQRXSB72EUDRGGZB&Expires=1600841263&Signature=ChdcYROKYj55w6jM7lIIX6b9UGo%3D";
  let { file, type } = req.body;

  console.log(req);

  let res2;
  if (file) {
    res2 = await fetch(signedUrl, {
      method: "PUT",
      body: file,
      headers: {
        "Content-Type": type,
      },
    });
  } else {
    res.status(500).json({ statusText: "No image selected" });
    return;
  }
  res.json(res2);
};
